function merge_families(familyData: any[], parent1Id: string, parent2Id: string) => {
    try {
      const mergedArray = familyData.filter(arr => arr.includes(parent1Id) || arr.includes(parent2Id)).flat();
      const familyDataUpdated = familyData.filter(arr => !arr.includes(parent1Id) && !arr.includes(parent2Id));
      familyDataUpdated.push(mergedArray)
      return familyDataUpdated
    } catch (error) {
      console.error("[merge_families] error", { familyData, parent1Id, parent2Id, error });
    }
  }
